/* @Author: Luiz Gustavo Freire Gama => http://github.com/luizgamabh :: Small fix for browsers that always rounds down line-height measures. */
(function(a){a.fn.lineHeightFix=function(){return this.each(function(){var b=a(this).css("line-height").replace(/\s?px/,"");isNaN(b)||a(this).css("line-height",Math.round(b)+"px")})};a(document).ready(function(){a("*").lineHeightFix()})})(jQuery);